import React from 'react'
import { StyleSheet, Text, View } from 'react-native';

const clock = (props) => {
    const parseTime = () => {
        const time = props.time
        const div = 60*60*60//*100
        const hours = Math.floor(time/div)
        const minutes = Math.floor((hours%1)*60)
        const seconds = Math.floor((minutes%1)*60)
        const miliseconds = seconds%1
        return ({
            hours: hours,
            minutes: minutes,
            seconds: seconds,
            miliseconds: miliseconds
        })
    }

    const styledTime = () => {
        const parsed = parseTime()
        return (
            <div>
                <Text>{parsed.hours}</Text><Text>{parsed.minutes}</Text><Text>{parsed.seconds}</Text>
            </div>
        )
    }
    return (
        <div style={{background: '#A51B02'}}>
        <Text> {props.run ? (props.type === 'timer' ? props.goal-props.time : props.time): props.goal}</Text>
        {styledTime()}
        </div>

    )
}

export default clock
