import React from 'react'
import { StyleSheet, Text, View } from 'react-native';

const clock = (props) => {
    const parseTime = () => {
        const div = 60*60*60*100
        const hours = floor(time/div)
        const minutes = floor((hours%1)*60)
        const seconds = floor((minutes%1)*60)
        const miliseconds = seconds%1
        return ({
            hours: hours,
            minutes: minutes,
            seconds: seconds,
            miliseconds: miliseconds
        })
    }

    const styledTime = () => {
        const parsed = parseTime()
        return (
            <div>
                <span>{parsed.hours}</span><span>{parsed.minutes}</span>
            </div>
        )


    }
    return (
        <Text> {props.run ? (props.type === 'timer' ? props.goal-props.time : props.time): props.goal}</Text>
    )
}

export default clock
