import React from 'react'
import { Dimensions, StyleSheet, Text, View } from 'react-native';

const clock = (props) => {
    const parseTime = () => {
        const time = props.type === 'timer' ? props.goal - props.time : props.time
        const div = 60*60//*100
        const hours = time/div
        const minutes = (hours%1)*60
        const seconds = (minutes%1)*60
        console.log('[CLOCK]', hours, minutes, seconds) 
        const miliseconds = seconds%1
        return ({
            hours: Math.floor(hours),
            minutes: Math.floor(minutes),
            seconds: Math.floor(seconds),
            miliseconds: miliseconds
        })
    }

    const styledTime = () => {
        const parsed = parseTime()
        //console.log('[PARSED]', parsed)
        return (
            <View style={styles.time}>
                <Text style={styles.text}>{parsed.hours}</Text><Text style={styles.text}>{parsed.minutes}</Text><Text style={[styles.text, styles.seconds]}>{parsed.seconds}</Text>
            </View>
        )
    }
    return (
        <View style={styles.clock}>
        <Text> {props.run ? (props.type === 'timer' ? props.goal-props.time : props.time): props.goal}</Text>
        {styledTime()}
        </View>

    )
}

const styles = StyleSheet.create({
    text: {
        color: '#ffffff',
        fontSize: 30
    },
    time: {
        //backgroundColor: '#A51B02',
        color: '#ffffff',
        fontSize: 30,
        display: 'flex',
        flexDirection: 'row',
    },
    seconds: {
        //color: '#ffffff',
        fontSize: 25,
        alignSelf: 'flex-end'
    },
    clock: {
        width: Dimensions.get('window').width * 0.9,
        height: Dimensions.get('window').width * 0.9,
        borderRadius: '50%',
        backgroundColor: '#A51B02',

    }
})

export default clock
