import React from 'react'
import { StyleSheet, Text, View } from 'react-native';

const clock = (props) => {
    const parseTime = () => {
        const time = props.type === 'timer' ? props.goal - props.time : props.time
        const div = 60*60//*100
        const hours = time/div
        const minutes = (hours%1)*60
        const seconds = (minutes%1)*60
        console.log('[CLOCK]', hours, minutes, seconds) 
        const miliseconds = seconds%1
        return ({
            hours: Math.floor(hours),
            minutes: Math.floor(minutes),
            seconds: Math.floor(seconds),
            miliseconds: miliseconds
        })
    }

    const styledTime = () => {
        const parsed = parseTime()
        //console.log('[PARSED]', parsed)
        return (
            <View style={style.clock}>
                <Text>{parsed.hours}</Text><Text>{parsed.minutes}</Text><Text>{parsed.seconds}</Text>
            </View>
        )
    }
    return (
        <View style={{background: '#A51B02'}}>
        <Text> {props.run ? (props.type === 'timer' ? props.goal-props.time : props.time): props.goal}</Text>
        {styledTime()}
        </View>

    )
}

const style = StyleSheet.create({
    text: {
        color: '#ffffff',
        fontSize: 30
    },
    clock: {
        backgroundColor: '#A51B02',
        color: '#ffffff',
        fontSize: 30,
        flexFlow: 'row',
    }
})

export default clock
