import React, { useState, useEffect } from 'react'
import { StyleSheet, Text, View } from 'react-native';

import Clock from '../../components/Clock/Clock'
import Boton from '../../components/Boton/Boton'
//Boton/

const Timer = (props) => {
    const [time, setTime] = useState(0)
    const [timeInter, setTimeInter] = useState(1000)
    const [run, setRun] = useState(false)
    const [goal, setGoal] = useState(25*60*1000)

    let interval

    const runClock = () => {
        interval = setInterval(passTime, timeInter)
    }

    const passTime = () => {
        const newTime=time+1
        setTime(time + 1)
        console.log('[PASS]', time)
    }

    const stopClock = () => {
        clearInterval(interval)
    }

    return (
        <View>
            <Clock goal={goal} run={run} time={time} />
            <Boton click={runClock} text="run"/>
            <Boton click={stopClock} text="stop"/>
        </View>

    )
}

export default Timer
